import * as Phaser from "phaser";
import { GridControls } from "./GridControls";
import { GridPhysics } from "./GridPhysics";
import { Player } from "./Player";
import { Client } from "../SocketClient";
const sceneConfig: Phaser.Types.Scenes.SettingsConfig = {
  active: false,
  visible: false,
  key: "Game",
};

export class GameScene extends Phaser.Scene {
  static readonly CANVAS_WIDTH = 720;
  static readonly CANVAS_HEIGHT = 528;

  static readonly TILE_SIZE = 48;

  private gridControls: GridControls;
  private gridPhysics: GridPhysics;

  constructor() {
    super(sceneConfig);
  }

  public create() {
    const client = new Client();
    client.sendTest();
    const cloudCityTilemap = this.make.tilemap({ key: "cloud-city-map" });
    cloudCityTilemap.addTilesetImage("Cloud City", "tiles");
    for (let i = 0; i < cloudCityTilemap.layers.length; i++) {
      const layer = cloudCityTilemap.createLayer(i, "Cloud City", 0, 0);
      layer.setDepth(i);
      layer.scale = 3;
    }

    const playerSprite = this.add.sprite(0, 0, "player");
    playerSprite.setDepth(2);

    this.cameras.main.startFollow(playerSprite);

    this.gridPhysics = new GridPhysics(
      new Player(playerSprite, 6, 8, 8),
      cloudCityTilemap
    );
    this.gridControls = new GridControls(this.input, this.gridPhysics);
  }

  public update(_time: number, delta: number) {
    this.gridControls.update();
    this.gridPhysics.update(delta);
  }

  public preload() {
    try {
      this.load.image("tiles", "assets/cloud_tileset.png");
      this.load.tilemapTiledJSON("cloud-city-map", "assets/cloud_city.json");
      this.load.spritesheet("player", "assets/characters.png", {
        frameWidth: Player.SPRITE_FRAME_WIDTH,
        frameHeight: Player.SPRITE_FRAME_HEIGHT,
      });
    } catch (e) {
      console.log(e);
    }
  }
}

const gameConfig: Phaser.Types.Core.GameConfig = {
  title: "Sample",
  render: {
    antialias: false,
  },
  type: Phaser.AUTO,
  scene: GameScene,
  scale: {
    width: GameScene.CANVAS_WIDTH,
    height: GameScene.CANVAS_HEIGHT,
    autoCenter: Phaser.Scale.CENTER_BOTH,
  },

  parent: "game",
  backgroundColor: "#48C4F8",
};

export const game = new Phaser.Game(gameConfig);
